sys_script_include.script:
var SnippetNow_DAO = Class.create();
SnippetNow_DAO.prototype = {
    initialize: function() {
    },

	getTypeApps: function(tableName) {
		/******************
		* Get either Types or Applications
		* return records
		******************/
		var record = [];
		var gr = new GlideRecordSecure(tableName);
		gr.query();
		while(gr.next()) {
			record.push({
				sys_id: gr.sys_id.toString(),
				label: gr.getValue('name'),
				color: gr.getValue('color'),
				count: this.getCount(tableName)
			});
		}
		return record;

	},
	getCount: function(tableName, encodedQuery) {
		/******************
		* Get counts
		* return int
		******************/
		var c = 0;
		var count = new GlideAggregate(tableName);

		if (encodedQuery) {
			count.getEncodedQuery(encodedQuery);
		}
		var count = new GlideAggregate(tableName);
		count.addAggregate('COUNT');
		count.query();

		if (count.next()) 
		   c = count.getAggregate('COUNT');

		return c;
	},

    type: 'SnippetNow_DAO'
};
